import-csv $CSV -Encoding UTF8 | foreach-object {New-ADUser -Name ($_.DisplayName) -SamAccountName ($_.DisplayName) -GivenName $_.GivenName -Surname $_.GivenName -City $_.City -Department $_.Department -DisplayName $_.DisplayName -Fax $_.Fax -MobilePhone $_.MobilePhone -Office $_.Office -PasswordNeverExpires ($_.PasswordNeverExpires -eq "True") -OfficePhone $_.PhoneNumber -PostalCode $_.PostalCode -EmailAddress $_.SignInName -State $_.State -StreetAddress $_.StreetAddress -Title $_.Title -UserPrincipalName $_.UserPrincipalName -AccountPassword (ConvertTo-SecureString -string $PWD -AsPlainText -force) -enabled $true }